namespace Unilab\Bannerslider\Controller\Adminhtml;

/**
 * Slider Abstract Action
 * @category Unilab
 * @package  Unilab_Bannerslider
 * @module   Bannerslider
 * @author   Unilab Developer
 */
abstract class Slider extends \Unilab\Bannerslider\Controller\Adminhtml\AbstractAction
{
    const PARAM_CRUD_ID = 'slider_id';

    /**
     * Check if admin has permissions to visit related pages.
     *
     * @return bool
     */
    protected function _isAllowed()
    {
        return $this->_authorization->isAllowed('Unilab_Bannerslider::bannerslider_sliders');
    }
}
